create table machines (ute tinyint not null check (ute between 0 and 4), id blob not null, tag varchar(255) not null unique, technology varchar(255) not null, primary key (id));
create table service_order_responsible (responsible_id blob not null, service_order_id blob not null, primary key (responsible_id, service_order_id));
create table service_orders (concluded boolean not null, duration_in_minutes integer not null, specialty tinyint not null check (specialty between 0 and 2), type tinyint not null check (type between 0 and 4), created_at timestamp, date timestamp not null, updated_at timestamp, id blob not null, machine_id blob, problem_description varchar(300) not null, solution_description varchar(300) not null, week_code varchar(255), primary key (id));
create table user (register integer not null, roule tinyint not null check (roule between 0 and 3), id blob not null, first_name varchar(255) not null, last_name varchar(255) not null, password varchar(255) not null, primary key (id));
create table worker (specialty tinyint not null check (specialty between 0 and 2), id blob not null, user_id blob, primary key (id));
